# https://leetcode.com/problems/longest-duplicate-substring/description/

'''
n: len of string 
Lower Bound: Output: n 
Lower Bound: Task: n 
Upper Bound: Naive: O(n^3) 
Upper Bound: TLE: quadratic 
'''

'''
Binary Search
k - ranges from 1 to n 
k - has a pattern like True, True, False, False, False 
Aim is to find the maximum True 
Time complexity: O(logn * n)
if is_duplicate_substring_possible 
l = mid 
else: 
r = mid 
'''

'''
Dynamic Programming 
base case: 
i >= n or j >= n: return 0 
general case: 
f(i, j) = 1 + f(i+1, j+1), if s1[i1] == s2[i2]
f(i, j) = 0, otherwise 
'''
class Solution:
    def is_substring_dup(self, k: int, s: str): 
        hsh = {}
        hash_code = 0
        base_v = 256
        p = 999979
        base_v_k = pow(base_v, k-1, p)
        for ind in range(k): 
            hash_code = (hash_code * base_v + ord(s[ind])) % p
        hsh[hash_code] = [0]
        # print(s)
        # print(hash_code, s[:k])
            
        for ind in range(k, len(s)): 
            # sub_str = s[ind-k: ind]
            pop_c = s[ind-k]
            add_c = s[ind]
            hash_code = (base_v * (hash_code - ord(pop_c)*base_v_k) + ord(add_c)) % p
            # print(hash_code, s[ind-k+1: ind+1])
            # print(ind-k, ind, sub_str)
            ind_lst = hsh.get(hash_code, [])
            for j in ind_lst: 
                if s[j: j + k] == s[ind-k+1: ind+1]:
                    return True, (ind+1)
            ind_lst.append(ind-k+1)
            hsh[hash_code] =  ind_lst
        return False, -1
        
    def longestDupSubstring(self, s: str) -> str:
        n = len(s)
        if n <= 1: return ''
        l, r = 1, n
        longest_sub_str_ind = -1
        # print(l, r)
        while (r-l) > 1: 
            mid = (l+r)//2 
            is_dup, sub_str_ind = self.is_substring_dup(mid, s)
            # print(mid, is_dup, sub_str_ind)
            if is_dup: 
                l = mid 
                longest_sub_str_ind = sub_str_ind
            else: 
                r = mid 
        # print(l, r, longest_sub_str_ind)
        if longest_sub_str_ind < 0: 
            is_dup, sub_str_ind = self.is_substring_dup(l, s)
            # print(l, is_dup, sub_str)
            return s[sub_str_ind - l: sub_str_ind] if is_dup else ''
        return s[longest_sub_str_ind - l: longest_sub_str_ind]  

        
    def longestDupSubstring2(self, s: str) -> str:
        n = len(s)
        if n == 1: return ''
        memo = {}
        def rec_longest_dup(i, j): 
            if i >= n or j >= n: return 0
            if (i, j) in memo: 
                return memo[(i, j)]
            if i != j and s[i] == s[j]:
                val = 1 + rec_longest_dup(i+1, j+1)
            else: 
                val = 0
            memo[(i, j)] = val
            return  val

        longest_ind = -1
        longest_len = -1
        for i1 in range(n): 
            for i2 in range(i1+1, n): 
                curr_longest_len = rec_longest_dup(i1, i2)
                if curr_longest_len > longest_len: 
                    longest_len = curr_longest_len 
                    longest_ind = i1

        return s[longest_ind: longest_ind + longest_len]

sol = Solution()
sol.longestDupSubstring("")  